mapper.select:
SELECT ifnull(SUM(c.price * c.amount), 0) money
FROM cart_board c, TMouseBoard p
WHERE c.idx = p.idx AND c.payment = 'N' AND c.user_id = #{user_id}

mapper.update:
UPDATE cart_board    
SET amount = #{amount} 
WHERE user_id= #{user_id} 
AND idx = #{idx}

mapper.select:
SELECT COUNT(*)
FROM cart_board
WHERE user_id= #{user_id} 
AND idx = #{idx}

